import { UrdfDefaultOptions } from './UrdfTypes.ts';
/**
 * A Color element in a URDF.
 */
export default class UrdfColor {
    /**
     * Color Red, [0, 1]
     */
    r: number;
    /**
     * Color Green, [0, 1]
     */
    g: number;
    /**
     * Color Blue, [0, 1]
     */
    b: number;
    /**
     * Alpha/Opacity, [0, 1]
     */
    a: number;
    constructor({ xml }: UrdfDefaultOptions);
}
